import React, { useState, useEffect } from 'react';
import { Grid, Card, CardContent, Typography, TextField, Button, Alert } from '@mui/material';
import axios from 'axios';

// Strategii
class Operation {
  execute(balance, params) {
    throw new Error("Metoda 'execute' trebuie implementată.");
  }
}

class Deposit extends Operation {
  execute(savings, amount) {
    return savings + amount;
  }
}

class Withdraw extends Operation {
  execute(savings, amount) {
    if (amount > savings) {
      throw new Error("Sold insuficient pentru retragere!");
    }
    return savings - amount;
  }
}

class GoalCalculator extends Operation {
  execute(savings, { goal, months, rate }) {
    const futureBalance = savings * Math.pow(1 + rate / 100, months);
    if (futureBalance >= goal) {
      return `Obiectivul de ${goal} RON va fi atins în ${months} luni.`;
    }
    const needed = goal - futureBalance;
    return `Mai ai nevoie de ${needed.toFixed(2)} RON pentru a atinge obiectivul.`;
  }
}

// Funcție generală pentru operare
const performOperation = (strategy, savings, params) => {
  try {
    return strategy.execute(savings, params);
  } catch (error) {
    return error.message;
  }
};

const SavingsAccountPage = () => {
  const [balance, setBalance] = useState(0); // Soldul curent
  const [savings, setSavings] = useState(0); // Economiile curente
  const [amount, setAmount] = useState(''); // Suma introdusă
  const [goal, setGoal] = useState(''); // Obiectivul financiar
  const [rate, setRate] = useState(''); // Rata dobânzii
  const [months, setMonths] = useState(''); // Numărul de luni
  const [result, setResult] = useState(''); // Mesajul rezultat
  const [error, setError] = useState(''); // Eroare

  // Fetch initial balance and savings from backend
  useEffect(() => {
    const fetchBalanceAndSavings = async () => {
      try {
        const response = await axios.get('http://localhost:8002/api/balance');
        if (response.data.balance !== undefined && response.data.savings !== undefined) {
          setBalance(response.data.balance);
          setSavings(response.data.savings);
        }
      } catch (err) {
        console.error('Error fetching balance and savings:', err);
      }
    };
    fetchBalanceAndSavings();
  }, []);

  useEffect(() => {
    fetchBalanceAndSavings();
  }, []);
  
  const handleDeposit = async () => {
    setError('');
    setResult('');
    try {
      const response = await axios.post('http://localhost:8002/api/deposit', {
        amount: parseFloat(amount),
      });
      if (response.data.success) {
        setBalance(response.data.balance);
        setSavings(response.data.savings);
        setResult(`Depunere reușită! Economii actualizate: ${response.data.savings} RON`);
        fetchBalanceAndSavings();
      } else {
        setError(response.data.error || 'Eroare la depunere');
      }
    } catch (err) {
      console.error('Error during deposit:', err);
      setError('Eroare la depunere');
    }
  };

  const handleWithdraw = async () => {
    setError('');
    setResult('');
    try {
      const response = await axios.post('http://localhost:8002/api/withdraw', {
        amount: parseFloat(amount),
      });
      if (response.data.success) {
        setBalance(response.data.balance);
        setSavings(response.data.savings);
        setResult(`Retragere reușită! Economii actualizate: ${response.data.savings} RON`);
        fetchBalanceAndSavings();
      } else {
        setError(response.data.error || 'Eroare la retragere');
      }
    } catch (err) {
      console.error('Error during withdrawal:', err);
      setError('Eroare la retragere');
    }
  };

  const handleGoalCalculation = () => {
    setError('');
    setResult('');
    const message = performOperation(new GoalCalculator(), savings, {
      goal: parseFloat(goal),
      months: parseInt(months, 10),
      rate: parseFloat(rate),
    });
    setResult(message);
  };

  return (
    <Grid container justifyContent="center" sx={{ padding: 3 }}>
      <Grid item xs={12} sm={8}>
        <Card sx={{ backgroundColor: '#686a6d', borderRadius: 2 }}>
          <CardContent>
            <Typography variant="h4" align="center" color="white" gutterBottom>
              Cont Economii
            </Typography>

            {/* Sold curent și Economii */}
            <Typography variant="h5" color="white" align="center">
              Sold curent: {`${balance} RON`}
            </Typography>
            <Typography variant="h6" color="white" align="center">
              Economii: {`${savings} RON`}
            </Typography>

            {/* Depunere și Retragere */}
            <TextField
              label="Suma"
              variant="outlined"
              fullWidth
              value={amount}
              onChange={(e) => setAmount(e.target.value)}
              type="number"
              sx={{ marginTop: 2 }}
            />
            <Button
              onClick={handleDeposit}
              variant="contained"
              sx={{ margin: 1, backgroundColor: '#4e6363', '&:hover': { backgroundColor: '#3c4e4d' } }}
            >
              Depunere
            </Button>
            <Button
              onClick={handleWithdraw}
              variant="contained"
              sx={{ margin: 1, backgroundColor: '#4e6363', '&:hover': { backgroundColor: '#3c4e4d' } }}
            >
              Retragere
            </Button>

            {/* Calcularea obiectivului financiar */}
            <TextField
              label="Obiectiv"
              variant="outlined"
              fullWidth
              value={goal}
              onChange={(e) => setGoal(e.target.value)}
              type="number"
              sx={{ marginTop: 2 }}
            />
            <TextField
              label="Rată dobândă (%)"
              variant="outlined"
              fullWidth
              value={rate}
              onChange={(e) => setRate(e.target.value)}
              type="number"
              sx={{ marginTop: 2 }}
            />
            <TextField
              label="Luni"
              variant="outlined"
              fullWidth
              value={months}
              onChange={(e) => setMonths(e.target.value)}
              type="number"
              sx={{ marginTop: 2 }}
            />
            <Button
              onClick={handleGoalCalculation}
              variant="contained"
              sx={{ margin: 1, backgroundColor: '#4e6363', '&:hover': { backgroundColor: '#3c4e4d' } }}
            >
              Calculează Obiectivul
            </Button>

            {/* Rezultate */}
            {result && (
              <Alert severity="success" sx={{ marginTop: 2 }}>
                {result}
              </Alert>
            )}
            {error && (
              <Alert severity="error" sx={{ marginTop: 2 }}>
                {error}
              </Alert>
            )}
          </CardContent>
        </Card>
      </Grid>
    </Grid>
  );
};

export default SavingsAccountPage;
